import React, { Component } from 'react';
import AppHeader from "../app-header";
import SearchInput from "../search-input";
import TaskList from "../task-list";
import './app.css'
import StatusFilter from "../status-filter";
import TaskInput from "../task-input";

class App extends Component {
    state = {
        tasks: [
            { id: 1, text: 'Buy bread' },
            { id: 2, text: 'Buy notebook' },
            { id: 3, text: 'Send homework nakonec to' },
            { id: 4, text: 'Do homework' }
        ]
    }

    // Define a method to handle adding tasks
    handleAddTask = (newTaskText) => {
        const { tasks } = this.state;
        const newTask = {
            id: tasks.length + 1,
            text: newTaskText
        };
        this.setState({ tasks: [...tasks, newTask] });
    };

    deleteItem = (id) => {
        this.setState(({ tasks }) => {
            const index = tasks.findIndex((e) => e.id === id)
            const newList = [...tasks.slice(0, index),
            ...tasks.slice(index + 1)]
            return {
                tasks: newList
            }
        })
    }

    setFilter = (filter) => {
        this.setState({ filter });
      };


    render() {
        return (
            <>
                <div className='app-todo'>
                    <AppHeader />

                    <div className='top-panel d-flex'>
                        <SearchInput placeText={"Search"} />
                        <StatusFilter
                            text='All'
                            active={'All'}
                            onClick={() => this.setFilter('All')}
                        />
                        <StatusFilter
                            text='Active'
                            active={'Active'}
                            onClick={() => this.setFilter('Active')}
                        />
                        <StatusFilter
                            text='Done'
                            active={'Done'}
                            onClick={() => this.setFilter('Done')}
                        />

                    </div>

                    <TaskInput onAddTask={this.handleAddTask} />

                    <TaskList
                        tasks={this.state.tasks}
                        onDeleted={this.deleteItem}
                    />
                </div>

            </>
        )
    }
}

export default App
