import React, { Component } from 'react';
import AppHeader from '../app-header';
import SearchInput from '../search-input';
import TaskList from '../task-list';
import './app.css';
import StatusFilter from '../status-filter';
import TaskInput from '../task-input';

class App extends Component {
    state = {
        tasks: [
            { id: 1, text: 'Buy bread', isComplete: false },
            { id: 2, text: 'Buy notebook', isComplete: false },
            { id: 3, text: 'Send homework nakonec to', isComplete: true },
            { id: 4, text: 'Do homework', isComplete: false },
        ],
        filter: 'All', // Default filter
    };

    // Function to set the filter
    setFilter = (filter) => {
        this.setState({ filter });
    };

    handleAddTask = (newTaskText) => {
        const { tasks } = this.state;
        const newTask = {
            id: tasks.length + 1,
            text: newTaskText,
            isComplete: false, // New tasks are initially not complete
        };
        this.setState({ tasks: [...tasks, newTask] });
    };

    deleteItem = (id) => {
        this.setState(({ tasks }) => {
            const index = tasks.findIndex((e) => e.id === id);
            const newList = [...tasks.slice(0, index), ...tasks.slice(index + 1)];
            return {
                tasks: newList,
            };
        });
    };

    render() {
        const { tasks, filter } = this.state;

        // Filter tasks based on the selected filter
        const filteredTasks =
            filter === 'Active'
                ? tasks.filter((task) => !task.isComplete)
                : filter === 'Done'
                ? tasks.filter((task) => task.isComplete)
                : tasks;

        return (
            <>
                <div className="app-todo">
                    <AppHeader />

                    <div className="top-panel d-flex">
                        <SearchInput placeText={'Search'} />
                        <StatusFilter text="All" active={filter === 'All'} onClick={() => this.setFilter('All')} />
                        <StatusFilter
                            text="Active"
                            active={filter === 'Active'}
                            onClick={() => this.setFilter('Active')}
                        />
                        <StatusFilter text="Done" active={filter === 'Done'} onClick={() => this.setFilter('Done')} />
                    </div>

                    <TaskInput onAddTask={this.handleAddTask} />

                    <TaskList tasks={filteredTasks} onDeleted={this.deleteItem} />
                </div>
            </>
        );
    }
}

export default App;
