import TaskListItem from "../task-list-item";
import './task-list.css'

import React, { Component } from 'react';

class TaskList extends Component {
    render() {
        const { onDeleted, tasks } = this.props;

        const elements = tasks.map((item) => {
            const { id, ...itemProps } = item;
            return (
                <li key={id} className="list-group-item">
                    <TaskListItem
                        {...itemProps}
                        onDeleted={() => {
                            onDeleted(id);
                        }}
                        onToggleComplete={() => {
                            // Toggle the isComplete state of the task in the parent component's state.
                            const updatedTasks = tasks.map((task) => {
                                if (task.id === id) {
                                    return { ...task, isComplete: !task.isComplete };
                                }
                                return task;
                            });

                            // Call the parent component's function to update the state.
                            onToggleComplete(updatedTasks);
                        }}
                    />
                </li>
            );
        });


        return (
            <ul className="list-group list-todo">
                {elements}
            </ul>
        )
    }
}

export default TaskList
