import React, { Component } from 'react';
import './task-list-item.css';

class TaskListItem extends Component {
    render() {
        const { text, isComplete, onDeleted, onToggleComplete } = this.props;

        const itemStyle = {
            textDecoration: isComplete ? 'line-through' : 'none',
        };

        return (
            <span className="todo-list-item">
                <span className="todo-list-item-text" style={itemStyle} onClick={onToggleComplete}>
                    {text}
                </span>
                <button type="button" className="btn btn-outline-danger btn-sm delete" onClick={onDeleted}>
                    Delete
                </button>
            </span>
        );
    }
}

export default TaskListItem;
