import TaskListItem from "../task-list-item";
import React, { Component } from 'react';

class TaskList extends Component {
    onToggleComplete = (id) => {
        // Find the task with the given id and toggle its isComplete property.
        const updatedTasks = this.props.tasks.map((task) => {
            if (task.id === id) {
                return { ...task, isComplete: !task.isComplete };
            }
            return task;
        });

        // Call the parent component's onToggleComplete function to update the state.
        this.props.onToggleComplete(updatedTasks);
    };

    render() {
        const { onDeleted, tasks } = this.props;

        const elements = tasks.map((item) => {
            const { id, ...itemProps } = item;
            return (
                <li key={id} className="list-group-item">
                    <TaskListItem
                        {...itemProps}
                        onDeleted={() => {
                            onDeleted(id);
                        }}
                        onToggleComplete={() => {
                            this.onToggleComplete(id);
                        }}
                    />
                </li>
            );
        });

        return (
            <ul className="list-group list-todo">
                {elements}
            </ul>
        );
    }
}

export default TaskList;
